        mealGoal = 0 // breakfast/lunch/dinner
        snackGoal = 0 // amount of snacks in a day
        BeverageGoal = 0 // amount of coffee/soda in a day/
        waterBottleSize = 0 // waterbootle size
        beverageSize = 0
